import React from "react";
import './switch.css';

function Switch( {handleCheckbox } ) {

    function handleCheckbox (checked) {
        setChecked(checked.target.checked);
        // changeMainColor();
    }

    return (
    <div className="dark-mode">
        <p className="dark-mode-title">Dark Mode</p>
        <input type="checkbox" onChange={handleCheckbox} className="checkbox" id="checkbox" />
        <label className="switch" htmlFor="checkbox">
        </label>
    </div>
    )
};

export { Switch } ;
