import React, {useState} from 'react';
import './globals.css';
import { Header } from './header';
import { TopCardList } from './top-card-list';
import { Overview } from './overview';
import { Switch } from './switch';

function App() {

  const [checked, setChecked] = React.useState(false);

  function handleCheckbox (checked) {
      setChecked(checked.target.checked);
      props.changeMainColor();
  }

  function changeMainColor() {
    console.log('Building a feature of the color in the main set');
  };

  return (
    <React.Fragment>
      <main className='mainClass'>
      <Header>
        <Switch
          changeMainColor={changeMainColor}
        />
      </Header>
      <TopCardList/>
      <Overview/>
      </main>
    </React.Fragment>
  );
}

export default App;
