  //     mq.addListener(changeMedia)
  //     setDarkMode(mq.matches)
  //     setChecked(mq.matches)
  //     return () => {
  //         mq.removeListener(changeMedia)
  //     }
  // }, [])

  return (
    <React.Fragment>
      <main className={mainClass}>
      <Header>
        <Switch
          setDarkMode={setDarkMode}
          checked={checked}
          setChecked={setChecked}
        />
      </Header>
      <TopCardList/>
      <Overview/>
      </main>
    </React.Fragment>
  );
}

export default App;
