import React from 'react';
import './globals.css';
import { Header } from './header';
import { TopCardList } from './top-card-list';
import { Overview } from './overview';
import { Switch } from './switch'

function App() {

  const [checked, setChecked] = React.useState('');

  const handleEventChecked = (event) => {
    setChecked(event.target.checked);
  }

  let valueClassName = '' ;
  const setupMode = () => {
    if (!handleEventChecked ) {
      valueClassName = 'is-dark-mode'
    } else {
      valueClassName = 'is-light-mode'
    }
  }

  return (
    <React.Fragment>
      <main className={setupMode}>
      <Header>
        <Switch
          checked={checked}
          setChecked={setChecked}
          handleEventChecked={handleEventChecked}
        />
      </Header>
      <TopCardList/>
      <Overview/>
      </main>
    </React.Fragment>
  );
}

export default App;
