        setChecked(checked);
    };

    return (
    <div className="dark-mode">
        <p className="dark-mode-title">Dark Mode</p>
        <input type="checkbox" onChange={handleCheckbox} checked={checked} className="checkbox" id="checkbox" />
        <label className="switch" htmlFor="checkbox">
        </label>
    </div>
    )
};

export { Switch } ;
