import React, { useRef, useEffect } from "react";
import './switch.css';

function Switch() {

    // Se actualiza sin necesidad de hacer un render para toda la aplicación
    const ref = useRef(null);
    function handleChange(event) {
        console.log(event.current.checked);

        if (ref.current.checked) {
            document.body.classList.remove('is-ligth-mode')
            document.body.classList.add('is-dark-mode')
        } else {
            document.body.classList.add('is-dark-mode')
            document.body.classList.remove('is-ligth-mode')
        }
    }

    useEffect(() => {
        const mq = window.matchMedia('(prefers-color-scheme: dark)')
        mq.addListener(changeMedia)
        setDarkMode(mq.matches)
        setChecked(mq.matches)
        return () => {
            mq.removeListener(changeMedia)
        }
    }, [])

    return (
        <div className='dark-mode'>
            <p className='dark-mode-title'>Dark Mode</p>
            <input ref={ref} onChange={handleChange} type='checkbox' className='checkbox' id='checkbox'/>
            <label className='switch' htmlFor='checkbox'>

            </label>
        </div>
    );
};

export { Switch } ;
