import React from "react";
import './switch.css';

function Switch( { checked , setChecked } ) {

    console.log(checked)

    function handleCheckbox (checked) {
        setChecked(checked.target.checked);
        console.log(checked)
    }

    return (
    <div className="dark-mode">
        <p className="dark-mode-title">Dark Mode</p>
        <input type="checkbox" onChange={handleCheckbox} checked={checked} className="checkbox" id="checkbox" />
        <label className="switch" htmlFor="checkbox">
        </label>
    </div>
    )
};

export { Switch } ;
